SELECT ewd.*, pwd.*\nFROM entry_weblog_data AS ewd\nLEFT JOIN weblog_data AS pwd_exact \n  ON pwd_exact.field_id_41 = ewd.field_id_32\nLEFT JOIN weblog_data AS pwd_like \n  ON (pwd_like.field_id_41 != '' \n      AND pwd_like.field_id_41 LIKE CONCAT('%', ewd.field_id_32, '%') \n      AND pwd_exact.field_id_41 IS NULL)\nWHERE pwd_exact.field_id_41 IS NOT NULL OR pwd_like.field_id_41 IS NOT NULL;