DECLARE @dbName NVARCHAR(128) = 'MyDB'; -- This would be dynamically set by the user or application logic.\nDECLARE @tableName NVARCHAR(128) = 'YourTableName';\nDECLARE @query NVARCHAR(MAX);\nDECLARE @resultTable TABLE (ID INT, Value NVARCHAR(50)); -- Define your result structure here.\n\nSET @query = N'\nSELECT ID, Value\nINTO #TempResult\nFROM ' + QUOTENAME(@dbName + '_2') + '.' + QUOTENAME('dbo') + '.' + QUOTENAME(@tableName) + ';\n\nINSERT INTO @resultTable (ID, Value)\nSELECT * FROM #TempResult;';\n\nEXEC sp_executesql @query, N'@resultTable TABLE READONLY', @resultTable OUTPUT;\n\n-- Now you can work with @resultTable as needed.