CREATE PROCEDURE log_wrapped_example (\n       p_param1 NUMBER,\n       p_param2 VARCHAR2\n   ) AS\n       l_parameters CLOB;\n   BEGIN\n       -- Format the parameters into a string or JSON structure\n       l_parameters := 'p_param1: ' || TO_CHAR(p_param1) || ', p_param2: ' || p_param2;\n\n       -- Insert log entry\n       INSERT INTO procedure_logs (proc_name, param_values)\n       VALUES ('example_procedure', l_parameters);\n\n       -- Call the original procedure\n       example_procedure(p_param1 => p_param1, p_param2 => p_param2);\n   END;