SELECT \n    T.SchedID,\n    LEVEL AS Payment_Num,\n    ADD_MONTHS(T.StartDate, (LEVEL-1) * T.Frequency) AS Due_Date,\n    T.PaymentAmt * LEVEL AS Expected_Total\nFROM \n    PaymentSchedule T\nCONNECT BY \n    PRIOR SchedID = SchedID\n    AND PRIOR StartDate = StartDate\n    AND PRIOR DBMS_RANDOM.VALUE IS NOT NULL -- To prevent rows from connecting to themselves\n    AND LEVEL <= T.Term / T.Frequency;