WITH test_values AS (\n    SELECT 19 AS num FROM dual UNION ALL\n    SELECT 20 FROM dual UNION ALL\n    SELECT 21 FROM dual\n)\nSELECT tv.num,\n       CASE \n           WHEN tv.num < 20 THEN (SELECT y FROM foo WHERE x = 10)\n           WHEN tv.num < 30 THEN (SELECT y FROM foo WHERE x = 20)\n           ELSE                  (SELECT y FROM foo WHERE x = 30)\n       END AS mapped_value\nFROM test_values tv;