CREATE TRIGGER InsteadOfUpdateTrigger ON CombinedView\nINSTEAD OF UPDATE\nAS\nBEGIN\n    IF EXISTS(SELECT 1 FROM inserted WHERE SourceTable = 'Table1')\n        UPDATE Table1\n        SET Column = i.Column -- set correct columns here\n        FROM inserted i\n        WHERE Table1.ID = i.ID; \n\n    IF EXISTS(SELECT 1 FROM inserted WHERE SourceTable = 'Table2')\n        UPDATE Table2\n        SET Column = i.Column -- set correct columns here\n        FROM inserted i\n        WHERE Table2.ID = i.ID;\nEND;