-- Trigger on INSERT\nCREATE TRIGGER trigger_name_after_insert\nON table_name\nAFTER INSERT\nAS\nBEGIN\n    -- Logic for handling the insert\nEND\n\n-- Trigger on UPDATE\nCREATE TRIGGER trigger_name_after_update\nON table_name\nAFTER UPDATE\nAS\nBEGIN\n    -- Logic for handling the update\nEND\n\n-- Trigger on DELETE\nCREATE TRIGGER trigger_name_after_delete\nON table_name\nAFTER DELETE\nAS\nBEGIN\n    -- Logic for handling the delete\nEND