-- Create a function to split a string into words using any non-alphanumeric character as delimiter\nCREATE FUNCTION [dbo].[SplitWords] (@input_string NVARCHAR(MAX))\nRETURNS @output_table TABLE (Word NVARCHAR(256))\nAS\nBEGIN\n    DECLARE @start INT, @end INT\n\n    SET @input_string = '%' + @input_string + '%'\n    SET @start = PATINDEX('%[^a-zA-Z0-9]%', @input_string)\n\n    WHILE @start > 0\n    BEGIN\n        SET @end = PATINDEX('%[a-zA-Z0-9][^a-zA-Z0-9]%', RIGHT(@input_string, LEN(@input_string) - (@start - 1)))\n\n        IF @end > 0\n        BEGIN\n            INSERT INTO @output_table (Word)\n            SELECT SUBSTRING(RIGHT(@input_string, LEN(@input_string) - (@start - 1)), 1, @end)\n\n            SET @start = PATINDEX('%[^a-zA-Z0-9]%', RIGHT(@input_string, LEN(@input_string) - (@start - 1 + @end)))\n        END\n        ELSE\n            BREAK\n    END\n\n    RETURN;\nEND;\n\n-- Use the function in your query\nDECLARE @searchTerm NVARCHAR(256) = 'Break'\n\nSELECT BusinessName\nFROM (\n    SELECT BusinessName, Word\n    FROM YourTable t\n    CROSS APPLY [dbo].[SplitWords](t.BusinessName)\n) AS split_words\nWHERE Word LIKE @searchTerm + '%'\nGROUP BY BusinessName;