DECLARE @job_id UNIQUEIDENTIFIER;\nDECLARE @step_id INT;\nDECLARE @command NVARCHAR(MAX);\nDECLARE @new_command NVARCHAR(MAX);\n\n-- Cursor to loop through each job step\nDECLARE job_cursor CURSOR FOR \nSELECT job_id, step_id, command \nFROM msdb.dbo.sysjobssteps \nWHERE command LIKE '%recipient@example.com%';\n\nOPEN job_cursor;\n\nFETCH NEXT FROM job_cursor INTO @job_id, @step_id, @command;\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    -- Remove the specific email address from the command text\n    SET @new_command = REPLACE(@command, 'recipient@example.com', '');\n\n    -- Update the step with the new command\n    EXEC msdb.dbo.sp_update_jobstep \n        @job_id = @job_id,\n        @step_id = @step_id,\n        @command = @new_command;\n\n    FETCH NEXT FROM job_cursor INTO @job_id, @step_id, @command;\nEND\n\nCLOSE job_cursor;\nDEALLOCATE job_cursor;