WITH RECURSIVE cte AS (\n    SELECT id, data, next_id, 1 as lvl\n    FROM LinkedList WHERE next_id IS NULL -- Start from the last node if you want it in reverse order or modify logic to start from head\n    UNION ALL\n    SELECT l.id, l.data, l.next_id, cte.lvl + 1\n    FROM LinkedList l\n    INNER JOIN cte ON l.next_id = cte.id\n)\nSELECT data FROM cte ORDER BY lvl DESC; -- Order by level descending if started from tail