CREATE PROCEDURE GetRecords\n    @IncludeSecondTable BIT = 0  -- Default is to not include the second table\nAS\nBEGIN\n    SELECT Column1, Column2, ...  -- List all columns from your tables\n    FROM Table1\n    WHERE SomeCondition = 'Value'  -- Replace with actual condition for Table1\n\n    UNION\n\n    SELECT Column1, Column2, ...  -- Ensure column list matches the first SELECT\n    FROM Table2\n    WHERE @IncludeSecondTable = 1 AND SomeCondition = 'Value'  -- Use parameter to control inclusion of Table2\nEND