CREATE OR REPLACE PROCEDURE fetch_data_by_id (\n    p_some_id IN VARCHAR2,\n    p_result OUT SYS_REFCURSOR\n) AS\nBEGIN\n    OPEN p_result FOR\n        SELECT info_field, data_field FROM table_one WHERE some_id = p_some_id\n        UNION ALL\n        SELECT info_field, data_field FROM table_two WHERE some_id = p_some_id\n        UNION ALL\n        SELECT info_field, data_field FROM table_three WHERE some_id = p_some_id\n        -- Add more tables here if needed\n        ;\nEND fetch_data_by_id;\n/