DELIMITER //\nCREATE PROCEDURE AppendToLinkedList(IN dataValue VARCHAR(255))\nBEGIN\n    DECLARE lastId INT;\n\n    -- Find the current last node\n    SELECT id INTO lastId FROM LinkedList WHERE next_id IS NULL LIMIT 1;\n\n    IF lastId IS NOT NULL THEN\n        -- Insert the new node and update the previous last node's next_id\n        INSERT INTO LinkedList (data, next_id) VALUES (dataValue, NULL);\n        UPDATE LinkedList SET next_id = LAST_INSERT_ID() WHERE id = lastId;\n    ELSE\n        -- If list is empty, insert as first node\n        INSERT INTO LinkedList (data, next_id) VALUES (dataValue, NULL);\n    END IF;\nEND //\nDELIMITER ;