CREATE TABLE dbo.DatabaseVersion (\n    VersionNumber INT PRIMARY KEY,\n    Description NVARCHAR(255),\n    AppliedDate DATETIME NOT NULL DEFAULT GETDATE()\n);\n\n-- Example script to add a new column\nGO\n\nDECLARE @CurrentVersion INT = 1;\n\nIF NOT EXISTS(SELECT * FROM dbo.DatabaseVersion WHERE VersionNumber = @CurrentVersion)\nBEGIN\n    BEGIN TRANSACTION;\n    \n    ALTER TABLE dbo.YourExistingTable ADD NewColumn NVARCHAR(255);\n    \n    INSERT INTO dbo.DatabaseVersion (VersionNumber, Description)\n    VALUES (@CurrentVersion, 'Added new column NewColumn to YourExistingTable');\n    \n    COMMIT TRANSACTION;\nEND\nGO