SELECT \n    p.ProductID,\n    p.ProductName,\n    MAX(CASE WHEN pm.Property = 'Color' THEN pm.Value ELSE NULL END) AS Color,\n    MAX(CASE WHEN pm.Property = 'Size' THEN pm.Value ELSE NULL END) AS Size,\n    MAX(CASE WHEN pm.Property = 'Material' THEN pm.Value ELSE NULL END) AS Material\nFROM \n    Products p\nLEFT JOIN \n    ProductMeta pm ON p.ProductID = pm.ProductID\nGROUP BY \n    p.ProductID, p.ProductName;