CREATE TABLE Customers (\n    CustomerId INT PRIMARY KEY IDENTITY,\n    CustomerType VARCHAR(50) NOT NULL, -- Discriminator column\n    CommonField1 VARCHAR(255),\n    CommonField2 VARCHAR(255),\n    -- Add more common fields here\n);\n\nCREATE TABLE BusinessCustomerDetails (\n    CustomerId INT PRIMARY KEY,\n    BusinessSpecificField1 NVARCHAR(255),\n    BusinessSpecificField2 NVARCHAR(255),\n    FOREIGN KEY (CustomerId) REFERENCES Customers(CustomerId)\n);\n\nCREATE TABLE IndividualCustomerDetails (\n    CustomerId INT PRIMARY KEY,\n    IndividualSpecificField1 NVARCHAR(255),\n    IndividualSpecificField2 NVARCHAR(255),\n    FOREIGN KEY (CustomerId) REFERENCES Customers(CustomerId)\n);\n\nCREATE TABLE Orders (\n    OrderId INT PRIMARY KEY IDENTITY,\n    CustomerId INT NOT NULL,\n    OrderDetails NVARCHAR(MAX),\n    FOREIGN KEY (CustomerId) REFERENCES Customers(CustomerId)\n);