-- Step 1: Add ngu_id column\nALTER TABLE master_table ADD ngu_id RAW(16);\nALTER TABLE child_table1 ADD ngu_id RAW(16);\n\n-- Steps 2-3: Populate ngu_id and update child references\nUPDATE master_table m\nSET ngu_id = generate_new_guid(m.gu_id); -- Assume a function that generates new GUID\n\nUPDATE child_table1 c\nSET ngu_id = (SELECT ngu_id FROM master_table WHERE gu_id = c.gu_id);\n\n-- Step 4: Drop Constraints\nALTER TABLE master_table DROP PRIMARY KEY;\nALTER TABLE child_table1 DROP CONSTRAINT foreign_key_name;\n\n-- Steps 5-6: Swap Columns and recreate constraints\nRENAME COLUMN master_table.gu_id TO old_gu_id;\nRENAME COLUMN master_table.ngu_id TO gu_id;\n\nALTER TABLE master_table ADD CONSTRAINT pk_master PRIMARY KEY (gu_id);\n\nALTER TABLE child_table1 RENAME COLUMN ngu_id TO gu_id;\nALTER TABLE child_table1 ADD CONSTRAINT fk_child FOREIGN KEY (gu_id) REFERENCES master_table(gu_id);