CREATE OR REPLACE PROCEDURE some_procedure IS\n    v_call_stack VARCHAR2(4000);\n    v_procedure_name VARCHAR2(32);\nBEGIN\n    v_call_stack := DBMS_UTILITY.FORMAT_CALL_STACK;\n    -- Extract the procedure name from the call stack\n    FOR i IN 1..REGEXP_COUNT(v_call_stack, 'PLS') LOOP\n        v_procedure_name := REGEXP_SUBSTR(\n            SUBSTR(v_call_stack, INSTR(v_call_stack, 'PLS-', 1, i)),\n            '[^ ]+', 4);\n        \n        -- Exit loop after finding the first procedure name\n        EXIT WHEN v_procedure_name LIKE 'some_procedure%';\n    END LOOP;\n\n    DBMS_OUTPUT.PUT_LINE('Procedure Name: ' || v_procedure_name);\nEND;\n/