-- Step 1: Convert BLOB data to Base64 in SQL Server 2005\nDECLARE @BinaryData VARBINARY(MAX);\nDECLARE @Base64 NVARCHAR(MAX);\n\n-- Assume 'YourTable' and 'BlobColumn' are the table name and the column with BLOB data respectively.\nSELECT @BinaryData = BlobColumn \nFROM YourTable \nWHERE SomeCondition = 'SomeValue'; -- Adjust as necessary\n\n-- Convert binary to base64\nSET @Base64 = CAST(N'' AS XML).value('xs:base64Binary(sql:variable("@BinaryData"))', 'NVARCHAR(MAX)');\n\nSELECT @Base64; -- This will give you the base64 string that can be included in your script\n\n-- Step 2 and 3: Insert the base64 string into another database\nDECLARE @InsertScript NVARCHAR(MAX);\nSET @InsertScript = N'INSERT INTO TargetTable (BlobColumn) VALUES (CAST(N''' + @Base64 + ''' AS XML).value(''xs:base64Binary(.)'', ''VARBINARY(MAX)''));';\n\n-- Save and then run @InsertScript on the target database\nSELECT @InsertScript;