SELECT \n    pm_info.is_read, \n    sender.usrFirst AS sender_name,\n    pm_data.date_sent, \n    pm_data.title, \n    pm_data.thread_id\nFROM \n    pm_info\nINNER JOIN \n    (SELECT thread_id, MAX(date_sent) AS latest_date FROM pm_data GROUP BY thread_id) AS latest_msgs ON pm_data.thread_id = latest_msgs.thread_id AND pm_data.date_sent = latest_msgs.latest_date\nINNER JOIN \n    tblUsers AS sender ON pm_data.sender_id = sender.usrID\nWHERE \n    pm_info.receiver_id = 2\nORDER BY \n    pm_data.date_sent DESC;