SELECT \n    u.UserID, \n    m.MovieID, \n    r.Rating\nFROM \n    (SELECT DISTINCT UserID FROM Ratings) AS u  -- Get all unique users\nCROSS JOIN \n    (SELECT DISTINCT MovieID FROM Ratings) AS m  -- Get all unique movies\nLEFT JOIN \n    Ratings r ON u.UserID = r.UserID AND m.MovieID = r.MovieID  -- Join with ratings table\nORDER BY \n    u.UserID, m.MovieID;  -- Order the results by UserID and then by MovieID