DECLARE MyCursor CURSOR FOR\nSELECT * \nFROM SCHOOL_DATA\nWHERE School = 'YourSchoolName' -- Filter for your school\nORDER BY [Order];\n\nOPEN MyCursor;\n\nFETCH NEXT FROM MyCursor INTO @RID, @School, @Order, @Text;\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    -- Process the row as needed\n    PRINT @Text; -- Example processing\n\n    FETCH NEXT FROM MyCursor INTO @RID, @School, @Order, @Text;\nEND\n\nCLOSE MyCursor;\nDEALLOCATE MyCursor;