import mx.controls.listClasses.List;
import mx.controls.dataGridClasses.DataGridListData;

function myItemRendererFunction(item:Object, column:DataGridColumn, listItemRendererType:int):Class {
if (item.type == "type1") {
return Type1Renderer;
} else if (item.type == "type2") {
return Type2Renderer;
}
return DefaultRenderer; // Default renderer for unknown types
}
