#!/bin/bash\ncd ~/basedir/files\nfor file in *.zip ; do\n    newfile=$(echo "${file}" | sed -e 's/^files.//' -e 's/.zip$//')\n    echo ":${newfile}:"\n    mkdir tmp\n    rm -rf "${newfile}"\n    mkdir "${newfile}"\n    cp "${newfile}.zip" tmp\n    cd tmp\n    unzip "${newfile}.zip"\n    find . -name '*.jpg' -exec cp {} "../${newfile}" ';'\n    find . -name '*.gif' -exec cp {} "../${newfile}" ';'\n    cd ..\n    rm -rf tmp\ndone