cat /tmp/1.txt | awk -F'\t' '\nBEGIN {\n    fieldSize=5;\n\n    for (i=1; i < fieldSize; i++) {\n    fLength[i]=0;\n    }\n}\n{\n    for (i=1; i <= fieldSize; i++) {\n    if (fLength[i] < length($i)) {\n            fLength[i] = length($i)\n        }\n\n    }\n    line[NR]=$0\n}\nEND {\n    for (i=1; i <= NR; i++) {\n        split(line[i], values, "\t")\n        format="| "\n        arguments=""\n        seperator="+"\n        for (j=1; j <= fieldSize; j++) {\n        format=format"%-"fLength[j]"s | "\n            for (c=0; c <= fLength[j]; c++) seperator=seperator"-"\n            seperator=seperator"-+"\n            arguments=arguments" \""values[j]"\""\n        }\n\n    if (i == 1) print seperator;\n\n        printCommand="printf \""format"\n\" "arguments\n        system(printCommand)\n\n    if (i == 1) print seperator;\n    }\n    print seperator;\n\n}\n'