using System;\nusing System.Collections.Generic;\nusing System.Linq;\nusing System.Text;\nusing System.DirectoryServices;\n\nnamespace Swapper\n{\n  class Program\n  {\n    static void Main(string[] args)\n    {\n      using (DirectoryEntry appRoot = \n               new DirectoryEntry("IIS://Localhost/W3SVC/1/root/app"))\n      {\n        switch (args[0].ToLower())\n        {\n          case "prod":\n            appRoot.Properties["Path"].Value = @"e:\app\prod";\n            appRoot.CommitChanges();\n            break;\n\n          case "rcx":\n            appRoot.Properties["Path"].Value = @"e:\app\rcx";\n            appRoot.CommitChanges();\n            break;\n\n          case "trunk":\n            appRoot.Properties["Path"].Value = @"e:\app\trunk";\n            appRoot.CommitChanges();\n            break;\n\n          default:\n            Console.WriteLine("Don't know");\n            break;\n        }\n      }\n    }\n  }\n}