var query = from a in aTable\n            where (from b in bTable\n                   where (from c in cTable\n                          where (from d in dTable\n                                 where dField == "a guid"\n                                 select d.cTableId)\n                                .Contains(c.cTableId)\n                          select c.bTableId)\n                         .Contains(b.bTableId)\n                   select b.aTableId)\n                   .Contains(a.aTableId)\n            select a;