var query = \n    from t in table\n    where id == 1\n    group t by new {\n                 t.Length,\n                 t.Width\n               } into g\n    orderby g.Key.Length, g.Key.Width\n    select new {\n             SIZES = (Convert.ToInt32(g.Key.Length) + " x " +\n                      Convert.ToInt32(g.Key.Width) + ", ")\n           };\n\nvar result = string.Join(string.Empty, query.Select(r => r.SIZES).ToArray());