public static IEnumerable<String> GetWords()\n{\n    for (Char c1 = 'A'; c1 <= 'Z'; c1++)\n    {\n        for (Char c2 = 'A'; c2 <= 'Z'; c2++)\n        {\n            for (Char c3 = 'A'; c3 <= 'Z'; c3++)\n            {\n                for (Char c4 = 'A'; c4 <= 'Z'; c4++)\n                {\n                    yield return "" + c1 + c2 + c3 + c4;\n                }\n            }\n        }\n    }\n}