var deletables =\n        from toDelete in db.NamedValues\n        where toDelete.Name == knownValue.Name\n        select toDelete;\n\n    using (var scope = new TransactionScope())\n    {\n        db.NamedValues.DeleteAllOnSubmit(deletables);\n        db.SubmitChanges();\n\n        db.NamedValues.InsertOnSubmit(knownValue);\n        db.SubmitChanges();\n\n        scope.Complete();\n    }