public class SpecialTextbox : TextBox\n{\n    public override void RenderControl(HtmlTextWriter writer)\n    {\n        if (!this.ReadOnly)\n        {\n            base.RenderControl(writer);\n        }\n        else\n        {\n            writer.Write(string.Format("<span id=\"{0}\" class=\"{1}\">{2}</span>", \n                            this.ClientID, \n                            this.CssClass, \n                            this.Text));\n        }\n    }\n}