$x = '31497230840470473074370324734723042.9';\n\nbcscale(100);\nvar_dump(bcFloor($x));\nvar_dump(bcCeil($x));\nvar_dump(bcRound($x));\n\nfunction bcFloor($x)\n{\n    $result = bcmul($x, '1', 0);\n    if ((bccomp($result, '0', 0) == -1) && bccomp($x, $result, 1))\n        $result = bcsub($result, 1, 0);\n\n    return $result;\n}\n\nfunction bcCeil($x)\n{\n    $floor = bcFloor($x);\n    return bcadd($floor, ceil(bcsub($x, $floor)), 0);\n}\n\nfunction bcRound($x)\n{\n    $floor = bcFloor($x);\n    return bcadd($floor, round(bcsub($x, $floor)), 0);\n}