<?php\n/**\n * English Number Converter - Collection of PHP functions to convert a number\n *                            into English text.\n *\n * This exact code is licensed under CC-Wiki on Stackoverflow.\n * http://creativecommons.org/licenses/by-sa/3.0/\n *\n * @link     http://stackoverflow.com/q/277569/367456\n * @question Is there an easy way to convert a number to a word in PHP?\n *\n * This file incorporates work covered by the following copyright and\n * permission notice:\n *\n *   Copyright 2007-2008 Brenton Fletcher. http://bloople.net/num2text\n *   You can use this freely and modify it however you want.\n */\n\nfunction convertNumber($number)\n{\n    list($integer, $fraction) = explode(".", (string) $number);\n\n    $output = "";\n\n    if ($integer{0} == "-")\n    {\n        $output = "negative ";\n        $integer    = ltrim($integer, "-");\n    }\n    else if ($integer{0} == "+")\n    {\n        $output = "positive ";\n        $integer    = ltrim($integer, "+");\n    }\n\n    if ($integer{0} == "0")\n    {\n        $output .= "zero";\n    }\n    else\n    {\n        $integer = str_pad($integer, 36, "0", STR_PAD_LEFT);\n        $group   = rtrim(chunk_split($integer, 3, " "), " ");\n        $groups  = explode(" ", $group);\n\n        $groups2 = array();\n        foreach ($groups as $g)\n        {\n            $groups2[] = convertThreeDigit($g{0}, $g{1}, $g{2});\n        }\n\n        for ($z = 0; $z < count($groups2); $z++)\n        {\n            if ($groups2[$z] != "")\n            {\n                $output .= $groups2[$z] . convertGroup(11 - $z) . (\n                        $z < 11\n                        && !array_search('', array_slice($groups2, $z + 1, -1))\n                        && $groups2[11] != ''\n                        && $groups[11]{0} == '0'\n                            ? " and "\n                            : ", "\n                    );\n            }\n        }\n\n        $output = rtrim($output, ", ");\n    }\n\n    if ($fraction > 0)\n    {\n        $output .= " point";\n        for ($i = 0; $i < strlen($fraction); $i++)\n        {\n            $output .= " " . convertDigit($fraction{$i});\n        }\n    }\n\n    return $output;\n}\n\nfunction convertGroup($index)\n{\n    switch ($index)\n    {\n        case 11:\n            return " decillion";\n        case 10:\n            return " nonillion";\n        case 9:\n            return " octillion";\n        case 8:\n            return " septillion";\n        case 7:\n            return " sextillion";\n        case 6:\n            return " quintrillion";\n        case 5:\n            return " quadrillion";\n        case 4:\n            return " trillion";\n        case 3:\n            return " billion";\n        case 2:\n            return " million";\n        case 1:\n            return " thousand";\n        case 0:\n            return "";\n    }\n}\n\nfunction convertThreeDigit($digit1, $digit2, $digit3)\n{\n    $buffer = "";\n\n    if ($digit1 == "0" && $digit2 == "0" && $digit3 == "0")\n    {\n        return "";\n    }\n\n    if ($digit1 != "0")\n    {\n        $buffer .= convertDigit($digit1) . " hundred";\n        if ($digit2 != "0" || $digit3 != "0")\n        {\n            $buffer .= " and ";\n        }\n    }\n\n    if ($digit2 != "0")\n    {\n        $buffer .= convertTwoDigit($digit2, $digit3);\n    }\n    else if ($digit3 != "0")\n    {\n        $buffer .= convertDigit($digit3);\n    }\n\n    return $buffer;\n}\n\nfunction convertTwoDigit($digit1, $digit2)\n{\n    if ($digit2 == "0")\n    {\n        switch ($digit1)\n        {\n            case "1":\n                return "ten";\n            case "2":\n                return "twenty";\n            case "3":\n                return "thirty";\n            case "4":\n                return "forty";\n            case "5":\n                return "fifty";\n            case "6":\n                return "sixty";\n            case "7":\n                return "seventy";\n            case "8":\n                return "eighty";\n            case "9":\n                return "ninety";\n        }\n    } else if ($digit1 == "1")\n    {\n        switch ($digit2)\n        {\n            case "1":\n                return "eleven";\n            case "2":\n                return "twelve";\n            case "3":\n                return "thirteen";\n            case "4":\n                return "fourteen";\n            case "5":\n                return "fifteen";\n            case "6":\n                return "sixteen";\n            case "7":\n                return "seventeen";\n            case "8":\n                return "eighteen";\n            case "9":\n                return "nineteen";\n        }\n    } else\n    {\n        $temp = convertDigit($digit2);\n        switch ($digit1)\n        {\n            case "2":\n                return "twenty-$temp";\n            case "3":\n                return "thirty-$temp";\n            case "4":\n                return "forty-$temp";\n            case "5":\n                return "fifty-$temp";\n            case "6":\n                return "sixty-$temp";\n            case "7":\n                return "seventy-$temp";\n            case "8":\n                return "eighty-$temp";\n            case "9":\n                return "ninety-$temp";\n        }\n    }\n}\n\nfunction convertDigit($digit)\n{\n    switch ($digit)\n    {\n        case "0":\n            return "zero";\n        case "1":\n            return "one";\n        case "2":\n            return "two";\n        case "3":\n            return "three";\n        case "4":\n            return "four";\n        case "5":\n            return "five";\n        case "6":\n            return "six";\n        case "7":\n            return "seven";\n        case "8":\n            return "eight";\n        case "9":\n            return "nine";\n    }\n}