$handle = fopen('blah', 'r');\n$blocks = array();\n$currentBlock = array();\nwhile (!feof($handle)) {\n    $line = fgets($handle);\n    if (trim($line) == '') {\n        if ($currentBlock) {\n            $blocks[] = $currentBlock;\n            $currentBlock = array();   \n        }\n    } else {\n        $currentBlock[] = $line;\n    }\n}\nfclose($handle);\n//if is anything left\nif ($currentBlock) {\n    $blocks[] = $currentBlock;   \n}\n\nprint_r($blocks);