$lines = explode( "\r\n", $data );\n\n$users = array();\n\nforeach( $lines as $line )\n{\n    $matches = array();\n    $user = array();\n\n    preg_match( "/([^ ]+) ([^ ]+) ((?:[A-Z])(?:[0-9]+:){3}[0-9]+) ([0-9]+) \/([0-9]+) ([0-9]+) \/ ([0-9]+)/", $line, $matches );\n\n    list(,$user['name'],$user['base'],$user['location'],$user['econ'],$user['maxecon'],$user['used'],$user['total']) = $matches;\n\n    $users[] = $user;\n}