function beforeSave() {\n    $this->recursive = -1;\n    $this->old = $this->find(array($this->primaryKey => $this->id));\n    if ($this->old){\n        $changed_fields = array();\n        foreach ($this->data[$this->alias] as $key =>$value) {\n            if ($this->old[$this->alias][$key] != $value) {\n                $changed_fields[] = $key;\n            }\n        }\n    }\n    // $changed_fields is an array of fields that changed\n    return true;\n}