$searchengines = array(\n    'Googlebot', \n    'Slurp', \n    'search.msn.com', \n    'nutch', \n    'simpy', \n    'bot', \n    'ASPSeek', \n    'crawler', \n    'msnbot', \n    'Libwww-perl', \n    'FAST', \n    'Baidu', \n    );\n$is_se = false;\nforeach ($searchengines as $searchengine){\n   if (!empty($_SERVER['HTTP_USER_AGENT']) and \n            false !== strpos(strtolower($_SERVER['HTTP_USER_AGENT']), strtolower($searchengine)))\n    {\n            $is_se = true;\n            break;\n    }\n}\nif ($is_se) { print('Its a search engine!'); } 