<?php\n    abstract class foo\n    {\n        public function __get($var)\n        {\n            echo "Parent (Foo) __get() called for $var\n";\n        }\n    }\n\n   class bar extends foo\n   {\n        private $var;\n        public function __construct()\n        {\n            $this->var = "25\n";\n        }\n\n        public function getVar()\n        {\n            return $this->var;\n        }\n    }\n\n    $obj = new bar();\n    echo $obj->var;\n    echo $obj->getVar();\n?>