session_start();\n\nclass Car {\n    public static $make;\n    public function __construct($make) {\n        self::$make = $make;\n    }\n}\n\n$c = new Car('Bugatti');\necho '<p>' . Car::$make . '</p>';\nunset($c);\n\nif (!isset($_SESSION['make'])) {\n    echo '<p>' . Car::$make . '</p>';\n    $c = new Car('Ferrari');\n    echo '<p>' . Car::$make . '</p>';\n}\n\n$_SESSION['make'] = Car::$make;\n\necho '<p>' . $_SESSION['make'] . '</p>';