$comments = '';\n$xml =<<<EOX\n<xml>\n    <!--data here -->\n    <data>\n        <!-- more here -->\n        <more />\n    </data>\n</xml>\nEOX;\n\n$reader = new XMLReader();\n$reader->XML($xml);\n\nwhile ($reader->read()) {\n  if ($reader->nodeType == XMLReader::COMMENT) {\n      $comments .= "\n".$reader->value;\n  }\n}\n\n$reader->close();\n\necho "all comments below:\n-------------------".$comments