function __construct($param) {\n    $id = null;\n    $email = null;\n    $username = null;\n    if (is_int($param)) {\n        // numerical ID was given\n        $id = $param;\n    } elseif (is_array($param)) {\n        if (isset($param['id'])) {\n            $id = $param['id'];\n        }\n        if (isset($param['email'])) {\n            $email = $param['email'];\n        }\n        if (isset($param['username'])) {\n            $username = $param['username'];\n        }\n    }\n}