$tree = array(\n    array('Food', 0),\n    array('Fruit', 1),\n    array('Red', 2),\n    array('Cherry', 3),\n    array('Strawberry', 3),\n    array('Cool', 4),\n    array('Not cool', 4),\n    array('Yellow', 2),\n    array('Banana', 3),\n    array('Meat', 0),\n    array('Beef', 1),\n    array('Pork', 1),\n);\n\n$depth = -1;\n$flag = false;\nforeach ($tree as $row) {\n    while ($row[1] > $depth) {\n        echo "<ul>\n", "<li>";\n        $flag = false;\n        $depth++;\n    }\n    while ($row[1] < $depth) {\n        echo "</li>\n", "</ul>\n";\n        $depth--;\n    }\n    if ($flag) {\n        echo "</li>\n", "<li>";\n        $flag = false;\n    }\n    echo $row[0];\n    $flag = true;\n}\nwhile ($depth-- > -1) {\n    echo "</li>\n", "</ul>\n";\n}