$foo = 'name=Shake & Bake&difficulty=easy';\n$pairs = preg_split('{&(?=[^\s])}',$foo);\n//$pairs = preg_split('/&(?=[^\s])/',$foo); //equivalent, using different delimiters.\n//$pairs = preg_split('%&(?=[^\s])%',$foo); //equivalent, using different delimiters.\n$done = Array();\nforeach($pairs as $keyvalue){\n    $parts = preg_split('{=}',$keyvalue);\n    $done[$parts[0]] = $parts[1];\n}\nprint_r($done); 