$postcode = trim(strip_tags($_REQUEST['postcode']));\n$test = $postcode;\nif(substr($test, -3) == " ") {\n  $postcode = $postcode; \n  } \n  else {\n  function stringrpl($x,$r,$str)\n    {\n    $out = "";\n    $temp = substr($str,$x);\n    $out = substr_replace($str,"$r",$x);\n    $out .= $temp;\n    return $out;\n    }\n\n    $test = stringrpl(-3," ",$test);\n$postcode = $test;\n  }