<?php\nclass ParentClass {\n\n    public $options;\n    public function __construct() {\n        $this->options = array(\n            'option1'=>'setting1'\n        );\n    }\n    //The rest of the functions would follow\n}\n\nclass ChildClass extends ParentClass {\n    public function __construct() {\n        parent::__construct();\n        $this->options['option2'] = 'setting2';\n    }\n    //The rest of the functions would follow\n}\n?>