<?php\n// set feed URL\n$lang = $_POST['language'];\n$query = $_POST['query'] . "%20review";\nswitch($lang){\n    case "English":\n        $feedURL = <<<URL\n        http://gdata.youtube.com/feeds/api/videos\n        ?q=<?php echo $query; ?>\n        &v=2\n        &format=5\n        &lr=en\n        URL;\n        break;\n    case "French":\n        $feedURL = <<<URL\n        http://gdata.youtube.com/feeds/api/videos\n        ?q=<?php echo $query; ?>\n        &v=2\n        &format=5\n        &lr=fr\n        URL;\n        break;\n    case "Spanish":\n        $feedURL = <<<URL\n        http://gdata.youtube.com/feeds/api/videos\n        ?q=$query;\n        &v=2\n        &format=5\n        &lr=es\n        URL;\n        break;\n}