function time_since($since) {\n    $chunks = array(\n        array(60 * 60 * 24 * 365 , 'year'),\n        array(60 * 60 * 24 * 30 , 'month'),\n        array(60 * 60 * 24 * 7, 'week'),\n        array(60 * 60 * 24 , 'day'),\n        array(60 * 60 , 'hour'),\n        array(60 , 'minute'),\n        array(1 , 'second')\n    );\n\n    for ($i = 0, $j = count($chunks); $i < $j; $i++) {\n        $seconds = $chunks[$i][0];\n        $name = $chunks[$i][1];\n        if (($count = floor($since / $seconds)) != 0) {\n            break;\n        }\n    }\n\n    $print = ($count == 1) ? '1 '.$name : "$count {$name}s";\n    return $print;\n}