abstract class ParentObj {\n    public function __construct(){}\n    public static function factory(){\n\n        //use php5.3 late static binding features:\n        $class=get_called_class();\n        return new $class;\n    }\n}\n\n\nclass ChildObj extends ParentObj{\n\n    function foo(){\n       echo "Hello world\n";\n    }\n\n}\n\n\n$child=ChildObj::factory();\n$child->foo();