public function query($queryString)\n    {\n\n        if (!$this->_connected) $this->_connectToDb(); //connect to database\n\n        $results = mysql_query($queryString, $this->_dbLink) or trigger_error(mysql_error());\n\n        $data = array();\n        while($row = mysql_fetch_assoc($results))\n         {\n               $data[] = $row;\n         }\n        return $data;\n\n    }