class ClassA {\n  function call() {\n    return $this->inner();\n  }\n  function inner() {\n    return "Class A";\n  }   \n}\nfunction ClassA() {\n  static $instance;\n  return $instance ? $instance : new ClassA();\n}\n\nclass ClassB extends ClassA {\n  function inner() {\n    return "Class B";\n  }\n}\nfunction ClassB() {\n  static $instance;\n  return $instance ? $instance : new ClassB();\n}\n\necho "<p>Class A = " . ClassA()->call();\necho "<p>Class B = " . ClassB()->call();